SELECT user_id, GROUP_CONCAT(role_id ORDER BY role_id) AS role_ids, GROUP_CONCAT(CONCAT( role_id, ':', role_name )) AS role_infos FROM ( SELECT user_role.user_id, user_role.role_id, role.role_name FROM user_role LEFT JOIN role ON user_role.role_id = role.id ) AS subquery WHERE user_id IN
#{userId}
GROUP BY user_id;
